mapper.select:
SELECT COUNT(a.advert_idx) FROM advert a
INNER JOIN qrboard q
ON a.qrboard_idx = q.qrboard_idx
WHERE a.user_idx = #{userIdx} AND a.use_yn = 'Y'


    AND DATE_FORMAT(a.reg_date, '%Y-%m-%d') >= #{startDate}




    AND DATE_FORMAT(a.reg_date, '%Y-%m-%d') <= #{endDate}



    AND a.advert_state = #{searchAdvertState}




            AND q.qrboard_name LIKE TRIM(CONCAT('%', #{searchValue}, '%'))


            AND a.advert_name LIKE TRIM(CONCAT('%', #{searchValue}, '%'))

SELECT a.advert_idx, a.advert_name, q.qrboard_name, a.advert_state, a.advert_sdate, a.advert_edate, a.reg_date FROM advert a
INNER JOIN qrboard q
ON a.qrboard_idx = q.qrboard_idx
WHERE a.user_idx = #{userIdx} AND a.use_yn = 'Y'


    AND DATE_FORMAT(a.reg_date, '%Y-%m-%d') >= #{startDate}




    AND DATE_FORMAT(a.reg_date, '%Y-%m-%d') <= #{endDate}



    AND a.advert_state = #{searchAdvertState}




            AND q.qrboard_name LIKE TRIM(CONCAT('%', #{searchValue}, '%'))


            AND a.advert_name LIKE TRIM(CONCAT('%', #{searchValue}, '%'))

mapper.insert:
INSERT INTO advert (
    qrboard_idx
    , qrboard_area_idx
    , content_idx
    , template_shop_idx
    , user_idx
    , advert_type
    , advert_name
    , advert_user_name
    , advert_user_ci
    , advert_user_tel
    , advert_sdate
    , advert_edate
    , advert_price
    , advert_profit
    , advert_pay_yn
    , advert_state
    , reg_date
) VALUES (
    #{qrboardIdx}
    , #{qrboardAreaIdx}
    , #{contentIdx}
    , #{templateShopIdx}
    , #{userIdx}
    , #{advertType}
    , #{advertName}
    , #{advertUserName}
    , #{advertUserCi}
    , #{advertUserTel}
    , #{advertSdate}
    , #{advertEdate}
    , #{advertPrice}
    , #{advertProfit}
    , #{advertPayYn}
    , #{advertState}
    , now()
)
